// Constants
export const SPACING_VALUE = 16;
export const BUTTON_BORDER_RADIUS = 6;

// Font sizes
export const fontSize = {
  small: 12,
  medium: 16,
  large: 20,
  extraLarge: 24,
};

// Colors
export const colors = {
  dark: '#000',
  white: '#fff',
  lightGray: '#f2f2f3',
  gray: '#8E8E93',
  green: '#32CD32',
  red: '#FF0000',
};

// Icon size
export const iconSize = {
  regular: 24,
  large: 40,
};
